# 프로그래머스# 두 정수 사이의 합def solution(a, b):    answer = 0    if a < b:        for i in range(a, b + 1):            answer += i    elif a > b:        for i in range(b, a+1):            answer += i    else:        answer = a    return answerif __name__ == "__main__":    a = 3    b = 3    print(solution(a, b))