# K번째 수# commands 내부에 순서대로 [i, j, k]를 표시함# i~j까지 자르고 정렬 그리고 k번째 수를 answer에 저장def solution(array, commands):    answer = []    for a in range(len(commands)):        i = commands[a][0]        j = commands[a][1]        k = commands[a][2]        sort = array[i - 1:j]        sort.sort()        data = sort[k - 1]        answer.append(data)    return answerif __name__ == "__main__":    array = [1, 5, 2, 6, 3, 7, 4]    commands = [[2, 5, 3], [4, 4, 1], [1, 7, 3]]    print(solution(array, commands))# 다른분의 풀이def result(array, commands):    return list(map(lambda x: sorted(array[x[0] - 1:x[1]])[x[2] - 1], commands))